import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function StudentTable() {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((data) => setStudents(data))
      .catch((err) => {
        console.log(err.message);
      });
  }, []);

  return (
    <div className="container">
      <h2 style={{ textAlign: "center" }}>Student Records</h2>
      <div className="table-container">
        <Link to="/student/create" className="btn btn-add">
          <button
            style={{
              backgroundColor: "white",
              color: "black",
              padding: "6px 12px",
              border: "none",
              borderRadius: "5px",
            }}
          >
            Add New Student
          </button>
        </Link>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Place</th>
              <th>Phone</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.id}>
                <td>{student.id}1</td>
                <td>{student.name}1</td>
                <td>{student.address}1</td>
                <td>{student.phone}1</td>
                <td>
                  <Link
                    to={`/student/view/${student.id}`}
                    className="btn"
                    style={{
                      display: "inline-block",
                      backgroundColor: "blue",
                      color: "white",
                      padding: "5px 12px",
                      margin: "0 2px",
                      borderRadius: "5px",
                      textDecoration: "none",
                    }}
                  >
                    View
                  </Link>
                  <Link
                    to={`/student/edit/${student.id}`}
                    className="btn"
                    style={{
                      display: "inline-block",
                      backgroundColor: "green",
                      color: "white",
                      padding: "5px 12px",
                      margin: "0 5px",
                      borderRadius: "2px",
                      textDecoration: "none",
                    }}
                  >
                    Edit
                  </Link>
                  <Link
                    className=""
                    style={{
                      backgroundColor: "red",
                      color: "white",
                      padding: "5px 12px",
                      margin: "0 2px",
                      border: "none",
                      borderRadius: "5px",
                    }}
                    onClick={() => {
                      console.log(`Delete student with ID: ${student.id}`);
                    }}
                  >
                    Delete
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
