import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function StudentTable() {
  const [students, setStudents] = useState("");
  useEffect(() => {
    fetch("http://localhost:3001/student")
      .then((res) => res.json())
      .then((data) => {
        setStudents(data).catch((err) => {
          console.log(err.message);
        });
      });
  }, []);

  return (
    <div className="container">
      <h2 style={{ textAlign: "center" }}>Student Records</h2>
      <div className="table-container">
        <Link to="/student/create" className="btn btn-add">
          <button
            style={{
              backgroundColor: "white",
              color: "black",
              padding: "6px 12px",
              border: "none",
              borderRadius: "5px",
            }}
          >
            Add New Student
          </button>
        </Link>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Place</th>
              <th>Phone</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {students &&
              students.map((item) => (
                <tr key={students.id}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.place}</td>
                  <td>{item.phone}</td>
                  <td>
                    <button
                      style={{
                        backgroundColor: "blue",
                        color: "white",
                        padding: "6px 12px",
                        border: "none",
                        borderRadius: "5px",
                      }}
                    >
                      <a href="" className="btn">
                        View
                      </a>
                    </button>
                    <button
                      style={{
                        backgroundColor: "green",
                        color: "white",
                        padding: "6px 12px",
                        border: "none",
                        borderRadius: "5px",
                      }}
                    >
                      <a href="" className="btn">
                        Edit
                      </a>
                    </button>
                    <button
                      style={{
                        color: "white",
                        backgroundColor: "red",
                        padding: "6px 12px",
                        border: "none",
                        borderRadius: "5px",
                      }}
                    >
                      <a href="" className="btn">
                        Delete
                      </a>
                    </button>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
